package Blind75;

public class LongestRepeatingCharReplaced {
    public int characterReplacement(String s, int k) {
        
        if(k >= s.length() - 2){
            return s.length();
        }

        int[] maxReplacementForChar = new int[26];

        // for each char in A - Z
        for(char currentChar = 'A'; currentChar <= 'Z'; currentChar++){
           
           int maxForChar = 0;
           int currentCount = 0;
           int kLeft = k;

           for(int i = 0; i < s.length(); i++ ){
            //    while(i < s.length() && (s.charAt(i) == currentChar || kLeft > 0)){
            //        if(s.charAt(i) != currentChar){
            //            kLeft--;
            //        }
            //        currentCount++;

            //        System.out.println(currentChar + " " + i + " " + s.charAt(i) + " " + currentCount + " " + kLeft);
            //        i++;
            //    }

                for(int j = i; j < s.length() && (s.charAt(j) == currentChar || kLeft > 0); j++){
                   if(s.charAt(j) != currentChar){
                       kLeft--;
                   }
                   currentCount++;

                //    System.out.println(currentChar + " " + j + " " + s.charAt(j) + " " + currentCount + " " + kLeft);
               }
                // System.out.println("For char " + currentChar + ", the max replacement so far is " + currentCount + " and remaining K is " + kLeft);
                maxForChar = Math.max(maxForChar, Math.min(s.length(), currentCount + kLeft));
                currentCount = 0;
                kLeft = k;
           }

           maxReplacementForChar[currentChar - 'A'] = maxForChar;
        //    System.out.println("For char " + currentChar + ", the max replacement is " + maxForChar);
        }

        // System.out.println(Arrays.toString(maxReplacementForChar));
    
        int maxRepeat = 0;

        for(int i : maxReplacementForChar){
            maxRepeat = maxRepeat < i ? i : maxRepeat;
        }

        return maxRepeat;

    }
    

    public static void main(String[] args){
        LongestRepeatingCharReplaced lr = new LongestRepeatingCharReplaced();

        System.out.println(lr.characterReplacement("", 7520));
    }
}
